    constructor(props) {
        super(props);
        this.state = {
            error: null,
            items: [],
            isLoaded: false
        }
    }

    componentDidMount() {
        fetch('http://localhost:5000/api/v1/schedule')
        .then(res => res.json())
        .then(
            (result) => {
                this.setState({
                    isLoaded: true,
                    items: result.items
